import { useContext } from 'react'
import shoplist from '../../assets/shop-store.json'
import { Productcontext } from '../../context/product.context'
import './shop-item.scss'
const Shopitem = () =>{

    const {products} = useContext(Productcontext);

    return(
        <div>
            {shoplist.map((item)=>{
                return(
                    <div className='shopitem-container' key={item.id}>{item.name}
                        <p>{item.price}</p>
                    <img className='background-image' 
                    src={item.imageUrl} 
                    alt = {`clothing type ${item.name}`}/>
                    {/* <div alt = {item.name} style={{backgroundImage :`url(${item.imageUrl})`}}></div> */}
                    </div>    
                )
            })}
        </div>
    )
}
export default Shopitem;
