import { auth,signinwithgooglePopup, signInWithGoogleRedirect } from "../../utilities/firebase.utils";
import { createUserDocFrom } from "../../utilities/firebase.utils"; //distingguish import funtion and component
import { useEffect } from "react";
import { getRedirectResult } from "firebase/auth";
import Forminput from "../../component/form-input/form-input.component";
import Signup from "../signup/signup.component";
import Button from "../../component/cutombutton/custombutton.component";
import './sign-in-block.scss'
////////开头大写才能被识别成component
const Signin = () => {

    useEffect(() => {
        async function fetchRedirectResult()
        {
            const response = await getRedirectResult(auth);
            if(response)
            {
                const {user} = response
                const userDocRef = await createUserDocFrom(user);
            } 
        }
        fetchRedirectResult();
    },[]);

    const signinGooglePopup = async () => {
        //获取google给的auth信息，内部有一个user参数，里面包含uid
        const {user}= await signinwithgooglePopup();

        const userDocRef = await createUserDocFrom(user);
        console.log("userdoc",userDocRef);
    }

    const handlesigninchange = () =>{

    }


    return(
        <div className="sign-in-block">
            <form className="group">
            <Forminput label="username" 
            inputoption = {{
                type : "text" ,
                required : true,
                onChange : handlesigninchange ,
                name : "username" ,
                value : "username" ,
                }}
            ></Forminput>
            <Forminput label="password" 
            inputoption = {{
                type : "password" ,
                required : true,
                onChange : handlesigninchange ,
                name : "password" ,
                value : "password" ,
                }}
            ></Forminput>
            </form>
            <Button buttontype="google" onClick={signinGooglePopup}>sign in with google</Button>
            <Button buttontype="inverted" onClick={signInWithGoogleRedirect}>google redirect</Button>

            <Signup/>
        </div>


    )

}

export default Signin;
