import './checkout.scss'
import { CartContext } from '../../context/cart-context.context';
import { useContext } from 'react';
import CheckoutItem from '../../component/checkout-item/checkout-item.component';

const Checkout = () => {
    const {cartItems,addItemToCart,removeItemfromCart,cartCount} = useContext(CartContext);
    return(
        <div className='checkout-container'>
            checkout page
            <div className='checkout-header'>
                    <div className='header-block'>
                        <span>product</span>
                    </div>
                    <div className='header-block'>
                    <span>description</span>
                    </div>
                    <div className='header-block'>
                    <span>quantity</span>
                    </div>
                    <div className='header-block'>
                    <span>price</span>
                    </div>
                    <div className='header-block'>
                    <span>remove</span>
                    </div>
                </div>
                {
                    cartItems.map(
                     (item) => ( <CheckoutItem key = {item.id} cartItem = {item} />)
                    )
                }
                <span className='Total'>Total : {cartCount}</span>   
        </div>
    )
}

export default Checkout;
