import { Outlet,Link} from 'react-router-dom';
import { Usercontext } from '../../context/user.context';
import { Fragment, useContext } from 'react';
import './navigation.styles.scss'


import {ReactComponent as Crownlogo} from '../../assets/crown.svg'//icon
import { SignoutUser } from '../../utilities/firebase.utils';
const Navigation = () =>{


    const {currentUser,setcurrentUser} = useContext(Usercontext);
    //logout
    const SignouHandler = async() =>{
       await SignoutUser()
       setcurrentUser(null);
    }
    console.log(currentUser);
    return(
    <Fragment>
        <div className='navigation-container'>
            <Link className='nav-link-logocontainer' to ='/'>    
                <Crownlogo/>           
            </Link>

            <div className='nav-links-container'>
                <Link className = 'nav-link' to ='/shop'>shop</Link>
                {
                currentUser ? ( <span onClick={SignouHandler} className='nav-link'>sign out</span>) :
                 (<Link className = 'nav-link' to ='/auth'>signin</Link>)
                }
            </div>

        </div>
        <Outlet/>
    </Fragment>
    )
}

export default Navigation;
