import { Fragment } from "react";
import { Outlet } from "react-router-dom";
import { useState } from "react";
import { createAuthUserWithEmailAndPassword } from "../../utilities/firebase.utils";
import "./signup.scss"
import { async } from "@firebase/util";
const defaultformcontent = [{
    username : '',
    email :'',
    password : '',
    confirmPassword :'',
}]

const Signup = () =>{

    //initialize formfields
    const [formfields,setformfields] = useState(defaultformcontent);
    //destrcuture formfields
    const {username,email,password,confirmPassword} = formfields;


    const handleChange = (event) =>{
        const {name,value } = event.target;
        setformfields({...formfields,[name]:value});
    }
    const SubmitHandler = async (event) =>
    {
        console.log("submit");
        event.preventDfault();
        if(password === confirmPassword)
        {
            try{
               const {user} = await createAuthUserWithEmailAndPassword(email,password);
               console.log(user);

            }
            catch(error)
            {
                console.error();
            }

        }
        else
        {
            alert("confirm password failed");
            console.log("confirm password wrong");
        }

    }
    return(
        <Fragment>
        <Outlet/>
        <div>
            <h1>sign up form</h1>
            <form onSubmit={SubmitHandler}>
                <label className="sign-up-label">Username</label>
                <input type="text" required onChange={handleChange} name = "username" value = {username}></input>

                <label className="sign-up-label">E-mail</label>
                <input type = "email" required onChange={handleChange} name = "email" value = {email}></input>

                <label className="sign-up-label">Password</label>
                <input type = "password" required onChange={handleChange} name = "password" value = {password}></input>

                <label className="sign-up-label">Confirm Password</label>
                <input type="password" required onChange={handleChange} name = "confirmPassword" value = {confirmPassword}></input>

                <button type="submit" >sign in</button>
            </form>
        </div>
        </Fragment>
    )
}

export default Signup;
